import React from 'react';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';

const ProductList = ({ products }) => {
  const dispatch = useDispatch();

  const addToCart = (product) => {
    dispatch({ type: 'ADD_TO_CART', payload: product });
  };

  return (
    <div className="container">
      <h2><br><br></br></br>Nossos Produtos:</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            <button onClick={() => addToCart(product)}>+ Carrinho</button>
          </li>
        ))}
      </ul>

      {/* Bot√£o para ir ao carrinho, agora com a classe "cart-button" para o estilo fixo */}
      <Link to="/cart">
        <button className="cart-button">Meu Carrinho</button>
      </Link>
    </div>
  );
};

export default ProductList;
